import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { makeStyles } from '@mui/styles';
import Grid from '@mui/material/Grid';
import NaturePic from '../img/nature1.jpeg';

const calStyle = makeStyles({
   cal: {
      display: 'absolute',
      position: 'absolute',
      textAlign: 'center',
      width: '100%',
      height: '100%',
      backgroundColor: '#F1F2ED',
      paddingTop: '30px',
  },
  pic: {
    width: '100%',

  }

})

export default function SelectLabels() {

  const classes = calStyle();

  // const countries = ['USA', 'Australia'];

  // const types = ['Electricity', 'Waste', 'Travel'];

  const [countries, setCountry] = React.useState('');
  const [types, setTypes] = React.useState('');

  const handleChangecountry = (event) => {
    setCountry(event.target.value);
  };

  const handleChangetype = (event) => {
    setTypes(event.target.value);
  };

  return (
    <div className={classes.cal}>
      <Grid container spacing={2}>
        <Grid item md={12}>
        <img src={NaturePic} alt='infoysys'className={classes.pic} />
        </Grid>
        <Grid item xs={12} md={6}>
          <FormControl sx={{ m: 1, minWidth: 120, width:180 }}>
            <InputLabel id="country-select">Country</InputLabel>
            <Select
              labelId="country-select-label"
              id="country-select"
              value={countries}
              label="Country"
              onChange={handleChangecountry}
            >
              <MenuItem value={10} backgroundColor='#000'>USA</MenuItem>
              <MenuItem value={20}>Australia</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12}md={6}>
          <FormControl sx={{ m: 1, minWidth: 120,  width:180 }}>
            <InputLabel id="type-select">Type</InputLabel>
            <Select
              labelId="type-select-label"
              id="type-select"
              value={types}
              label="Type"
              onChange={handleChangetype}
            >
              <MenuItem value={1}>Electricity</MenuItem>
              <MenuItem value={2}>Waste</MenuItem>
              <MenuItem value={3}>Travel</MenuItem>
            </Select>
          </FormControl>
        </Grid>
      </Grid>
    </div>
  );
}
